mapper.select:
select

from base



        and Name like #{namez}


        and Type = #{base.type}


        and Province = #{base.province}


        and City = #{base.city}


        and Hour >= #{base.starthour}


        and Hour <= #{base.endhour}

    and BaseStatus = 1

order by UpdateTime desc

select distinct (Type)
from base
where BaseStatus = 1;

select distinct (Province)
from base
where BaseStatus = 1;

select distinct (City)
from base
where Province = #{province} and BaseStatus = 1;

mapper.insert:
insert into base
( ID, Picture, Name
, Type, Content, Hour
, BaseStatus, CreateTime, UpdateTime
, Province, City)
values ( #{id,jdbcType=INTEGER}, #{picture,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}
       , #{type,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, #{hour,jdbcType=INTEGER}
       , #{basestatus,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}
       , #{province,jdbcType=VARCHAR}, #{city,jdbcType=VARCHAR})
